mapper.select:
SELECT depre_type_id AS depreTypeId, depre_type_name AS depreTypeName, depre_period AS deprePeriod, type_desc AS typeDesc, cal_method AS calMethod, create_time AS createTime, DATE_FORMAT(create_time,'%Y-%m-%d %H:%i:%s') AS createTimeStr, create_user_id AS createUserId, create_user_name AS createUserName, update_time AS updateTime, DATE_FORMAT(update_time,'%Y-%m-%d %H:%i:%s') AS updateTimeStr, update_user_id AS updateUserId, update_user_name AS updateUserName FROM admin_depre_type WHERE alive_flag = '1'
AND depre_type_id = #{depreTypeId}
AND depre_type_name LIKE ('${depreTypeName}')
AND cal_method = #{calMethod}
ORDER BY create_time DESC
ORDER BY ${sort} ${order}

SELECT a.assets_id AS assetsId, a.assets_no AS assetsNo, a.assets_name AS assetsName, a.model AS model, a.assets_type_url AS assetsTypeUrl, a.assets_type_id AS assetsTypeId, a.manu_facturer AS manuFacturer, a.manu_date AS manuDate, a.buy_date AS buyDate, a.be_dep AS beDep, a.custodian AS custodian, a.notes AS notes, a.remain_val_rate AS remainValRate, a.depre_type_id AS depreTypeId, b.depre_type_name AS depreTypeName, b.cal_method AS calMethod, b.depre_period AS deprePeriod, a.start_depre AS startDepre, DATE_FORMAT(a.start_depre,'%Y-%m-%d %H:%i:%s') AS startDepreStr, a.intend_term AS intendTerm, a.intend_work_gross AS intendWorkGross, a.work_gross_unit AS workGrossUnit, a.asset_value AS assetValue, a.asset_cur_value AS assetCurValue, a.depre_rate AS depreRate, a.def_per_work_gross AS defPerWorkGross, a.update_time AS updateTime, DATE_FORMAT(a.update_time,'%Y-%m-%d %H:%i:%s') AS updateTimeStr, a.update_user_id AS updateUserId, a.update_user_name AS updateUserName FROM admin_fixed_assets a, admin_depre_type b WHERE a.alive_flag = '1' AND a.depre_type_id = b.depre_type_id
AND a.assets_type_url LIKE ('${assetsTypeUrl}')
AND a.assets_id = #{assetsId}
AND a.assets_name LIKE ('${assetsName}')
AND a.be_dep LIKE ('${beDep}')
AND a.depre_type_id = #{depreTypeId}
ORDER BY a.create_time DESC
ORDER BY ${sort} ${order}
