mapper.update:
UPDATE 
	personnel_attendance_group
SET 
	update_time = NOW(),
	update_user_id = #{updateUserId},
 	update_user_name = #{updateUserName}

		,group_name = #{groupName}


		,default_on_time = #{defaultOnTime}


		,default_off_time = #{defaultOffTime}

WHERE
	alive_flag = '1'
AND group_id = #{groupId}

UPDATE
	user_info
SET
	update_time = NOW(),
	update_user_id = #{updateUserId},
	update_user_name = #{updateUserName},
	attendance_group_id = #{groupId}
WHERE
	alive_flag = '1'

 	AND attendance_group_id = #{attendanceGroupId}


 	AND user_id = #{userId}


 	AND user_account = #{userAccount}


	AND user_name LIKE (${userName})


	AND post_name LIKE (${postName})


	AND post_id = #{postId}


	AND department_name LIKE (${departmentName})


	AND department_url LIKE (${departmentUrl})


	AND id_card = #{idCard}


	AND sex = #{sex}


	AND work_time > STR_TO_DATE(#{workTimeStartStr},'%Y-%m-%d')


	AND work_time < STR_TO_DATE(#{workTimeEndStr},'%Y-%m-%d')		



		AND invite_flag = #{inviteFlag}


		AND (invite_flag = '1' OR invite_flag = '4')


		AND retire_flag = #{retireFlag}


		AND (retire_flag = '1' OR retire_flag = '3')






	AND political_status = #{politicalStatus}


	AND education = #{education}


	AND marriage_flag = #{marriageFlag}


	AND dorm_flag = #{dormFlag}


	AND work_time = #{workTime}


	AND english_level = #{englishLevel}


	AND wage_account = #{wageAccount}


	AND address LIKE(${address})


	AND native_place LIKE(${nativePlace})


	AND volk = #{volk}


	AND dorm = #{dorm}


	AND disease_history_flag = #{diseaseHistoryFlag}

UPDATE user_info SET update_time = NOW(), update_user_id = #{updateUserId}, update_user_name = #{updateUserName}, attendance_group_id = NULL WHERE alive_flag = '1'
AND user_id IN (${userIds})
AND attendance_group_id = #{attendanceGroupId}

mapper.select:
SELECT class_id AS classId, group_id AS groupId, class_date AS classDate, work_flag AS workFlag, on_duty_time AS onDutyTime, off_duty_time AS offDutyTime, create_user_id AS createUserId, create_user_name AS createUserName, create_time AS createTime, update_user_id AS updateUserId, update_user_name AS updateUserName, update_time AS updateTime, alive_flag AS aliveFlag FROM personnel_attendance_class WHERE alive_flag = '1'
AND group_id = #{groupId}
AND class_id = #{classId}
AND DATE_FORMAT(class_date,'%Y-%m') = #{queryMonth}
AND class_date = #{classDate}
AND work_flag = #{workFlag}

mapper.update:
UPDATE 
	personnel_attendance_class
SET 
	update_time = NOW(),
	update_user_id = #{updateUserId},
 	update_user_name = #{updateUserName}

		,on_duty_time = #{onDutyTime}


		,off_duty_time = #{offDutyTime}


		,work_flag = #{workFlag}

WHERE
	alive_flag = '1'


			AND class_date = #{classDate}
			AND group_id = #{groupId}



	AND class_id = #{classId}

UPDATE personnel_attendance_class SET update_time = NOW(), update_user_id = #{updateUserId}, update_user_name = #{updateUserName}, alive_flag = '0' WHERE alive_flag = '1'
AND group_id = #{groupId}
AND class_id = #{classId}
AND class_date = #{classDate}
