mapper.update:
UPDATE
	personnel_welfare
SET
		welfare_name = #{welfareName},
		text = #{text},
		give_time = STR_TO_DATE(#{giveTimeStr},'%Y-%m-%d %H:%i:%s')

		 ,population = #{population}


		 ,budget_amount = #{budgetAmount}


		 ,reason = #{reason}


WHERE
	welfare_id = #{welfareId}
	AND alive_flag = '1'

UPDATE personnel_welfare_record SET alive_flag = '0' WHERE alive_flag = '1'
AND welfare_record_id = #{welfareRecordId}
AND welfare_id = #{welfareId}

mapper.select:
SELECT
	user_id AS userId,
	user_name AS userName,
	id_card AS idCard
FROM
	user_info
WHERE
	alive_flag = '1'

 	AND user_id = #{userId}


 	AND user_account = #{userAccount}


	AND user_name LIKE (${userName})


	AND post_name LIKE (${postName})


	AND post_id = #{postId}


	AND department_name LIKE (${departmentName})


	AND department_url LIKE (${departmentUrl})


	AND id_card LIKE (${idCard})


	AND sex = #{sex}


	AND work_time > STR_TO_DATE(#{workTimeStartStr},'%Y-%m-%d %H:%i:%s')


	AND work_time < STR_TO_DATE(#{workTimeEndStr},'%Y-%m-%d %H:%i:%s')		


	AND invite_flag = #{inviteFlag}


	AND (invite_flag = '1' OR invite_flag = '4' OR invite_flag = '5')


	AND retire_flag = #{retireFlag}


	AND (retire_flag = '1' OR retire_flag = '3')


	AND political_status = #{politicalStatus}


	AND education = #{education}


	AND marriage_flag = #{marriageFlag}


	AND dorm_flag = #{dormFlag}


	AND dorm = #{dorm}


	AND work_time = #{workTime}

SELECT user_id AS userId, user_name AS userName, phone_num AS phoneNum, accident_phone_num AS accidentPhoneNum, birthday AS birthday, DATE_FORMAT(birthday,'%Y-%m-%d') AS birthdayStr, id_card AS idCard, sex AS sex, work_time AS workTime, DATE_FORMAT(work_time,'%Y-%m-%d') AS workTimeStr, address AS address, kpi_group_id AS kpiGroupId, leader_flag AS leaderFlag, create_time AS createTime, DATE_FORMAT(create_time,'%Y-%m-%d %H:%i:%s') AS createTimeStr, create_user_name AS createUserName, update_time AS updateTime, DATE_FORMAT(update_time,'%Y-%m-%d %H:%i:%s') AS updateTimeStr, update_user_name AS updateUserName, education AS education, volk AS volk, department_name AS departmentName, post_id AS postId, post_name AS postName, invite_flag AS inviteFlag, retire_flag AS retireFlag, political_status AS policalStatus, remark AS remark, native_place AS nativePlace, marriage_flag AS marriageFlag FROM user_info WHERE alive_flag = '1' AND invite_flag in('1','4','5')
AND (DATE_FORMAT(birthday, '%m-%d') BETWEEN #{startDate} AND #{endDate}
OR DATE_FORMAT(birthday, '%m-%d') BETWEEN #{spanYearStartDate} AND #{spanYearEndDate}
)
AND user_id = '${userId}'
AND political_status = '${politicalStatus}'
AND native_place = '${nativePlace}'
AND marriage_flag = '${marriageFlag}'
AND user_Name LIKE ${userName}
AND phone_num = '${phoneNum}'
AND accident_phone_num = '${accidentPhoneNum}'
AND id_card= '${idCard}'
AND sex = '${sex}'
AND address LIKE ${address}
AND kpi_group_id = '${kpiGroupId}'
AND leader_flag = '${leaderFlag}'
AND education = '${education}'
AND volk = '${volk}'
AND department_name LIKE ${departmentName}
AND post_id = '${postId}'
AND post_name LIKE ${postName}
AND invite_flag = '${inviteFlag}'
AND retire_flag = '${retireFlag}'
ORDER BY department_name,post_name,work_time DESC
ORDER BY ${sort} ${order}

mapper.update:
UPDATE personnel_welfare
SET
	update_time = NOW(),
	update_user_id = #{updateUserId},
	update_user_name = #{updateUserName}

		,examine_status = #{examineStatus}


		,record_user_id = #{recordUserId}
		,record_user_name = #{recordUserName}
		,record_time = NOW()


WHERE
	welfare_id = #{welfareId}

UPDATE personnel_welfare_stream
SET
	update_time = NOW(),
	update_user_id = #{updateUserId},
	update_user_name = #{updateUserName}

		,examine_status = #{examineStatus}
		,examine_time = NOW()


		,examine_idea = #{examineIdea}


		,examine_user_id = #{examineUserId}
		,examine_user_name = #{examineUserName}


		,next_examine_user_name = #{nextExamineUserName}

WHERE
	welfare_stream_id = #{welfareStreamId}
