mapper.select:
SELECT SUM(c.out_number) AS outTotalNumber, SUM(c.tax_amount) AS outTotalTaxAmount FROM repertory_goods a, repertory_out_record c WHERE c.alive_flag = '1' AND a.goods_id = c.goods_id
AND a.goods_type_url LIKE ('${goodsTypeUrl}')
AND a.total_number >= ${beginNumber}
AND a.total_number <= ${endNumber}
AND a.goods_name LIKE ('${goodsName}')
AND a.goods_size LIKE ('${goodsSize}')
AND c.goods_position_id = #{goodsPositionId}
AND c.goods_position_name = #{goodsPositionName}
AND c.repertory_id = #{repertoryId}
AND c.get_user_name LIKE ('${getUserName}')
AND c.operate_user_name LIKE ('${operateUserName}')
AND c.get_department_id = #{getDepartmentId}
AND c.out_time >= STR_TO_DATE(#{beginTime},'%Y-%m-%d %H') AND c.out_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H')

SELECT SUM(b.in_number) AS inTotalNumber, SUM(b.pretax_amount) AS inTotalPretaxAmount, SUM(b.tax_amount) AS inTotalTaxAmount FROM repertory_goods a, repertory_in_record b WHERE b.alive_flag = '1' AND a.goods_id = b.goods_id
AND a.goods_type_url LIKE ('${goodsTypeUrl}')
AND a.total_number >= ${beginNumber}
AND a.total_number <= ${endNumber}
AND a.goods_name LIKE ('${goodsName}')
AND a.goods_size LIKE ('${goodsSize}')
AND b.goods_position_id = #{goodsPositionId}
AND b.goods_position_name LIKE ('${goodsPositionName}')
AND b.repertory_id = #{repertoryId}
AND b.put_user_name LIKE ('${putUserName}')
AND b.operate_user_name LIKE ('${operateUserName}')
AND b.in_time >= STR_TO_DATE(#{beginTime},'%Y-%m-%d %H') AND b.in_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H')

SELECT SUM(b.in_number) AS inTotalNumber, SUM(b.pretax_amount) AS inTotalPretaxAmount, SUM(b.tax_amount) AS inTotalTaxAmount, SUM(c.out_number) AS outTotalNumber, SUM(c.tax_amount) AS outTotalTaxAmount FROM repertory_goods a, repertory_in_record b, repertory_out_record c WHERE b.alive_flag = '1' AND c.alive_flag = '1' AND a.goods_id = b.goods_id AND a.goods_id = c.goods_id
AND a.goods_type_url LIKE ('${goodsTypeUrl}')
AND a.total_number >= ${beginNumber}
AND a.total_number <= ${endNumber}
AND a.goods_name LIKE ('${goodsName}')
AND a.goods_size LIKE ('${goodsSize}')
AND b.goods_position_id = #{goodsPositionId} AND c.goods_position_id = #{goodsPositionId}
AND b.goods_position_name LIKE ('${goodsPositionName}') AND c.goods_position_name = #{goodsPositionName}
AND b.repertory_id = #{repertoryId} AND c.repertory_id = #{repertoryId}
AND b.put_user_name LIKE ('${putUserName}')
AND c.get_user_name LIKE ('${getUserName}')
AND b.operate_user_name LIKE ('${operateUserName}') AND c.operate_user_name LIKE ('${operateUserName}')
AND c.getdepartment_id = #{getDepartmentId}
AND b.in_time >= STR_TO_DATE(#{beginTime},'%Y-%m-%d %H') AND b.in_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H') AND c.out_time >= STR_TO_DATE(#{beginTime},'%Y-%m-%d %H') AND c.out_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H')

SELECT a.goods_id AS goodsId, a.goods_name AS goodsName, a.total_number AS totalNumber, a.goods_type_url AS goodsTypeUrl, a.goods_size AS goodsSize, a.unit AS unit, a.warn_number AS warnNumber, a.latest_unit_price AS latestUnitPrice, a.moving_aver_price AS movingAverPrice, a.weighted_aver_price AS weightedAverPrice, a.create_time AS createTime, a.create_user_id AS createUserId, a.create_user_name AS createUserName, a.update_time AS updateTime, a.update_user_id AS updateUserId, a.update_user_name AS updateUserName, b.in_record_id AS inRecordId, b.repertory_id AS repertoryId, b.repertory_name AS repertoryName, b.goods_position_id AS goodsPositionId, b.goods_position_name AS goodsPositionName, b.in_number AS inNumber, b.pretax_amount AS pretaxAmount, b.pretax_aver_price AS pretaxAverPrice, b.tax_rate AS taxRate, b.tax_amount AS taxAmount, b.tax_aver_price AS taxAverPrice, b.operate_user_id AS operateUserId, b.operate_user_name AS operateUserName, b.in_time AS inTime, b.put_user_id AS putUserId, b.put_user_name AS putUserName, b.record AS record, b.create_time AS createTime, b.create_user_id AS createUserId, b.create_user_name AS createUserName, b.update_time AS updateTime, b.update_user_id AS updateUserId, b.update_user_name AS updateUserName, b.provider_code AS providerCode, b.no_write_amount AS noWriteAmount, c.provider_name AS providerName, d.in_batch_flow_id AS inBatchFlowId, d.in_batch_flow_code AS inBatchFlowCode FROM repertory_goods a, repertory_in_record b LEFT JOIN repertory_provider c ON(c.provider_code = b.provider_code) LEFT JOIN repertory_in_batch_flow d ON(b.in_batch_flow_id = d.in_batch_flow_id)
a.alive_flag = '1' AND b.alive_flag = '1' AND a.goods_id = b.goods_id
AND b.in_record_id = #{inRecordId}
AND a.goods_name LIKE ('${goodsName}')
AND a.goods_size LIKE ('${goodsSize}')
AND a.goods_type_url LIKE ('${goodsTypeUrl}')
AND b.in_number >= ${beginNumber}
AND b.in_number <= ${endNumber}
AND b.operate_user_name LIKE ('${operateUserName}')
AND b.put_user_name LIKE ('${putUserName}')
AND b.in_time >= STR_TO_DATE(#{beginTime},'%Y-%m-%d %H') AND b.in_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H')
AND b.repertory_id = ${repertoryId}
AND b.goods_position_name LIKE ('${goodsPositionName}')
AND b.provider_code = #{providerCode}
AND d.in_batch_flow_code LIKE CONCAT(#{inBatchFlowCode},'%')
ORDER BY b.create_time DESC

SELECT a.goods_id AS goodsId, a.goods_name AS goodsName, a.total_number AS totalNumber, a.goods_type_url AS goodsTypeUrl, a.goods_size AS goodsSize, a.unit AS unit, a.warn_number AS warnNumber, a.latest_unit_price AS latestUnitPrice, a.moving_aver_price AS movingAverPrice, a.weighted_aver_price AS weightedAverPrice, a.create_time AS createTime, a.create_user_id AS createUserId, a.create_user_name AS createUserName, a.update_time AS updateTime, a.update_user_id AS updateUserId, a.update_user_name AS updateUserName, b.in_record_id AS inRecordId, b.repertory_id AS repertoryId, b.repertory_name AS repertoryName, b.goods_position_id AS goodsPositionId, b.goods_position_name AS goodsPositionName, b.in_number AS inNumber, b.pretax_amount AS pretaxAmount, b.pretax_aver_price AS pretaxAverPrice, b.tax_rate AS taxRate, b.tax_amount AS taxAmount, b.tax_aver_price AS taxAverPrice, b.operate_user_id AS operateUserId, b.operate_user_name AS operateUserName, b.in_time AS inTime, b.put_user_id AS putUserId, b.put_user_name AS putUserName, b.record AS record, b.create_time AS createTime, b.create_user_id AS createUserId, b.create_user_name AS createUserName, b.update_time AS updateTime, b.update_user_id AS updateUserId, b.update_user_name AS updateUserName, b.provider_code AS providerCode, b.no_write_amount AS noWriteAmount, b.invoice_id AS invoiceId, c.in_batch_flow_id AS inBatchFlowId, c.in_batch_flow_code AS inBatchFlowCode FROM repertory_goods a, repertory_in_record b LEFT JOIN repertory_in_batch_flow c ON(b.in_batch_flow_id = c.in_batch_flow_id)
a.alive_flag = '1' AND b.alive_flag = '1' AND a.goods_id = b.goods_id AND b.no_write_amount != '0' AND ISNULL(b.red_id)
AND b.in_record_id = #{inRecordId}
AND a.goods_name LIKE ('${goodsName}')
AND a.goods_size LIKE ('${goodsSize}')
AND a.goods_type_url LIKE ('${goodsTypeUrl}')
AND b.in_number >= ${beginNumber}
AND b.in_number <= ${endNumber}
AND b.operate_user_name LIKE ('${operateUserName}')
AND b.put_user_name LIKE ('${putUserName}')
AND b.in_time >= STR_TO_DATE(#{beginTime},'%Y-%m-%d %H') AND b.in_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H')
AND b.repertory_id = ${repertoryId}
AND b.goods_position_name LIKE ('${goodsPositionName}')
AND b.provider_code = #{providerCode}
AND c.in_batch_flow_code LIKE CONCAT(#{inBatchFlowCode},'%')
ORDER BY b.create_time DESC

SELECT a.goods_id AS goodsId, a.goods_type_id AS goodsTypeId, a.goods_name AS goodsName, 'inRecord' AS operation, a.total_number AS totalNumber, a.goods_type_url AS goodsTypeUrl, a.goods_size AS goodsSize, a.unit AS unit, a.warn_number AS warnNumber, a.latest_unit_price AS latestUnitPrice, a.moving_aver_price AS movingAverPrice, a.weighted_aver_price AS weightedAverPrice, b.in_record_id AS inRecordId, b.repertory_id AS repertoryId, b.repertory_name AS repertoryName, b.goods_position_id AS goodsPositionId, b.goods_position_name AS goodsPositionName, b.in_number AS inNumber, b.in_number AS outNumber, b.pretax_amount AS pretaxAmount, b.tax_amount AS taxAmount, b.in_time AS outTime, b.in_time AS inTime, b.operate_user_name AS operateUserName, b.put_user_name AS getUserName, b.put_user_name AS putUserName, b.record AS record, b.create_time AS createTime, b.create_user_id AS createUserId, b.create_user_name AS createUserName, b.update_time AS updateTime, b.update_user_id AS updateUserId, b.update_user_name AS updateUserName, '入库' AS type FROM repertory_goods a, repertory_in_record b LEFT JOIN repertory_in_batch_flow c ON(b.in_batch_flow_id = c.in_batch_flow_id) WHERE a.alive_flag = '1' AND b.alive_flag = '1' AND a.goods_id = b.goods_id
AND a.goods_type_url LIKE ('${goodsTypeUrl}')
AND a.total_number >= ${beginNumber}
AND a.total_number <= ${endNumber}
AND a.goods_name LIKE ('${goodsName}')
AND a.goods_size LIKE ('${goodsSize}')
AND b.goods_position_id = #{goodsPositionId}
AND b.goods_position_name LIKE ('${goodsPositionName}')
AND b.repertory_id = #{repertoryId}
AND b.put_user_name LIKE ('${putUserName}')
AND b.operate_user_name LIKE ('${operateUserName}')
AND b.repertory_id = '0'
AND b.in_time >= STR_TO_DATE(#{beginTime},'%Y-%m-%d %H') AND b.in_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H')
AND c.in_batch_flow_code LIKE CONCAT(#{inBatchFlowCode},'%')
UNION SELECT a.goods_id AS goodsId, a.goods_type_id AS goodsTypeId, a.goods_name AS goodsName, 'outRecord' AS operation, a.total_number AS totalNumber, a.goods_type_url AS goodsTypeUrl, a.goods_size AS goodsSize, a.unit AS unit, a.warn_number AS warnNumber, a.latest_unit_price AS latestUnitPrice, a.moving_aver_price AS movingAverPrice, a.weighted_aver_price AS weightedAverPrice, b.out_record_id AS outRecordId, b.repertory_id AS repertoryId, b.repertory_name AS repertoryName, b.goods_position_id AS goodsPositionId, b.goods_position_name AS goodsPositionName, b.out_number AS outNumber, b.out_number AS inNumber, b.pretax_amount AS pretaxAmount, b.tax_amount AS taxAmount, b.out_time AS outTime, b.out_time AS inTime, b.get_department_name AS getDepartmentName, b.get_user_name AS getUserName, b.get_user_name AS putUserName, b.record AS record, b.create_time AS createTime, b.create_user_id AS createUserId, b.create_user_name AS createUserName, b.update_time AS updateTime, b.update_user_id AS updateUserId, b.update_user_name AS updateUserName, '出库' AS type FROM repertory_goods a, repertory_out_record b LEFT JOIN repertory_out_batch_flow c ON(b.out_batch_flow_id = c.out_batch_flow_id) WHERE a.alive_flag = '1' AND b.alive_flag = '1' AND a.goods_id = b.goods_id
AND a.goods_type_url LIKE ('${goodsTypeUrl}')
AND total_number >= ${beginNumber} AND total_number <= ${endNumber}
AND a.goods_name LIKE ('${goodsName}')
AND a.goods_size LIKE ('${goodsSize}')
AND b.goods_position_id = #{goodsPositionId}
AND b.goods_position_name LIKE ('${goodsPositionName}')
AND b.repertory_id = #{repertoryId}
AND b.get_department_id = #{getDepartmentId}
AND b.get_department_id = '0'
AND b.out_time >= STR_TO_DATE(#{beginTime},'%Y-%m-%d %H') AND b.out_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H')
AND c.out_batch_flow_code LIKE CONCAT(#{outBatchFlowCode},'%')

mapper.update:
UPDATE
	repertory_provider
SET
	no_write_amount =  no_write_amount + #{noWriteAmount}
WHERE
	provider_code = #{providerCode}
