mapper.update:
UPDATE user_transfer_info SET alive_flag = '0', update_user_id = #{updateUserId}, update_user_name = #{updateUserName}, update_time = now() WHERE alive_flag = '1'
AND user_id = #{userId}
AND user_transfer_info_id = #{userTransferInfoId}

mapper.select:
SELECT user_id AS userId, user_name AS userName, id_card AS idCard, invite_flag AS inviteFlag FROM user_info WHERE alive_flag = '1'
AND user_id != #{userId}
AND id_card = #{idCard}

SELECT user_id AS userId, user_account AS userAccount, password AS password, user_name AS userName, phone_num AS phoneNum, accident_phone_num AS accidentPhoneNum, birthday AS birthday, DATE_FORMAT(birthday,'%Y-%m-%d') AS birthdayStr, id_card AS idCard, id_card_up_img AS idCardUpImg, id_card_down_img AS idCardDownImg, id_card_valid AS idCardValid, id_card_issued AS idCardIssued, photo AS photo, attendance_group_id AS attendanceGroupId,
b.group_name AS attendanceGroupName,
sex AS sex, work_time AS workTime, DATE_FORMAT(work_time,'%Y-%m-%d') AS workTimeStr, address AS address, kpi_group_id AS kpiGroupId, leader_flag AS leaderFlag, a.create_user_id AS createUserId, a.create_user_name AS createUserName, a.create_time AS createTime, a.update_user_id AS updateUserId, a.update_user_name AS updateUserName, a.update_time AS updateTime, a.alive_flag AS aliveFlag, education AS education, volk AS volk, department_url AS departmentUrl, department_name AS departmentName, post_id AS postId, post_name AS postName, invite_flag AS inviteFlag, retire_flag AS retireFlag, political_status AS politicalStatus, native_place AS nativePlace, marriage_flag AS marriageFlag, dorm AS dorm, dorm_flag AS dormFlag, english_level AS englishLevel, wage_account AS wageAccount, disease_history_flag AS diseaseHistoryFlag, disease_history AS diseaseHistory FROM user_info a
LEFT JOIN personnel_attendance_group b ON ( a.attendance_group_id = b.group_id )
WHERE a.alive_flag = '1'
AND user_id = #{userId}
AND user_account = #{userAccount}
AND user_name LIKE (${userName})
AND post_name LIKE (${postName})
AND post_id = #{postId}
AND department_name LIKE (${departmentName})
AND department_url LIKE (${departmentUrl})
AND id_card = #{idCard}
AND sex = #{sex}
AND work_time >= STR_TO_DATE(#{workTimeStartStr},'%Y-%m-%d')
AND work_time <= STR_TO_DATE(#{workTimeEndStr},'%Y-%m-%d')
AND birthday >= STR_TO_DATE(#{birthdayStartStr},'%Y-%m-%d')
AND birthday <= STR_TO_DATE(#{birthdayEndStr},'%Y-%m-%d')
AND (invite_flag = '1' OR invite_flag = '4' OR invite_flag= '5')
AND invite_flag = #{inviteFlag}
AND retire_flag = #{retireFlag}
AND user_id IN ( SELECT user_id FROM framework_user_role )
AND user_id NOT IN ( SELECT user_id FROM framework_user_role )
AND user_id IN ( SELECT user_id FROM personnel_dimission WHERE dimission_time >= str_to_date(#{dimissionTimeStart}, '%Y-%m-%d')
AND dimission_time <= str_to_date(#{dimissionTimeEnd}, '%Y-%m-%d')
)
AND attendance_group_id = #{attendanceGroupId}
AND (invite_flag = '1' OR invite_flag = '4') AND (retire_flag = '1' OR retire_flag = '3')
AND (invite_flag = '1' OR invite_flag = '4')
AND political_status = #{politicalStatus}
AND education = #{education}
AND marriage_flag = #{marriageFlag}
AND dorm_flag = #{dormFlag}
AND work_time = #{workTime}
AND english_level = #{englishLevel}
AND wage_account = #{wageAccount}
AND address LIKE(${address})
AND native_place LIKE(${nativePlace})
AND volk = #{volk}
AND dorm = #{dorm}
AND disease_history_flag = #{diseaseHistoryFlag}
AND id_card_down_img IS NOT NULL
AND id_card_down_img IS NULL
ORDER BY department_name, post_name,user_account
ORDER BY ${sort} ${order}

SELECT work_id AS workId, user_id AS userId, post_name AS postName, compony_name AS componyName, start_time AS startTime, end_time AS endTime, work_describe AS workDescribe, create_user_id AS createUserId, create_user_name AS createUserName, create_time AS createTime, update_user_id AS updateUserId, update_user_name AS updateUserName, update_time AS updateTime, alive_flag AS aliveFlag FROM personnel_doc_work WHERE alive_flag = '1'
AND work_id = #{workId}
AND user_id = #{userId}

SELECT train_doc_id AS trainDocId, train_name AS trainName, train_id AS trainId, user_id AS userId, user_name AS userName, start_time AS startTime, end_time AS endTime, train_describe AS trainDescribe, train_company AS trainCompany, create_user_id AS createUserId, create_user_name AS createUserName, create_time AS createTime, update_user_id AS updateUserId, update_user_name AS updateUserName, update_time AS updateTime, join_flag AS joinFlag, alive_flag AS aliveFlag FROM personnel_doc_train WHERE alive_flag = '1'
AND train_doc_id = #{trainDocId}
AND user_id = #{userId}

SELECT education_id AS educationId, user_id AS userId, school_name AS schoolName, specialty AS specialty, star_time AS startTime, end_time AS endTime, create_user_id AS createUserId, create_time AS createTime, update_user_id AS updateUserId, update_time AS updateTime, alive_flag AS aliveFlag, create_user_name AS createUserName, update_user_name AS updateUserName FROM personnel_doc_education WHERE alive_flag = '1'
AND user_id = #{userId}
AND education_id = #{educationId}

SELECT type, message, user_id AS userId, user_info_other_id AS userInfoOtherId FROM user_info_other WHERE alive_flag = '1'
AND user_id = #{userId}
AND user_info_other_id = #{userInfoOtherId}
